def is_woodall(num):
    if num < 1:
        return False

    n = 1
    while True:
        woodall = n * (2 ** n) - 1
        if woodall == num:
            return True
        if woodall > num:
            return False
        n += 1

# Example usage:
print(is_woodall(1))    # True, because 1 * 2^1 - 1 = 1
print(is_woodall(7))    # True, because 2 * 2^2 - 1 = 7
print(is_woodall(23))   # True, because 3 * 2^3 - 1 = 23
print(is_woodall(10))   # False, 10 is not a Woodall number

def div_even_odd(numbers):
    first_even = None
    first_odd = None

    for num in numbers:
        if first_even is None and num % 2 == 0:
            first_even = num
        if first_odd is None and num % 2 != 0:
            first_odd = num
        if first_even is not None and first_odd is not None:
            break

    if first_even is not None and first_odd is not None:
        return first_even / first_odd
    else:
        return None

# Example usage:
numbers = [1, 3, 5, 7, 8, 10]
result = div_even_odd(numbers)
print(result)  # Output: 8.0 (since 8 / 1 = 8.0)

def compute_last_digit(a: int, b: int) -> int:
    if a < b:
        return 0
    elif a == b:
        return 1
    else:
        # Compute the product (b+1) * (b+2) * ... * a modulo 10
        result = 1
        for i in range(b + 1, a + 1):
            result = (result * i) % 10
            if result == 0:
                break  # If result is 0, it will remain 0
        return result

# Example usage:
print(compute_last_digit(5, 3))  # Output: 2 (since 5! / 3! = 20, last digit is 0)
print(compute_last_digit(7, 7))  # Output: 1 (since 7! / 7! = 1, last digit is 1)
print(compute_last_digit(4, 5))  # Output: 0 (since 4! / 5! is not an integer)
