def is_woodall(num):
    n = 1
    while True:
        woodall_number = n * (2 ** n) - 1
        if woodall_number == num:
            return True
        elif woodall_number > num:
            return False
        n += 1

# Example usage:
print(is_woodall(7))  # True, because 7 is 2*2^2 - 1
print(is_woodall(15)) # False
print(is_woodall(31)) # True, because 31 is 3*2^3 - 1

def find_first_duplicate(arr):
    seen = set()
    for num in arr:
        if num in seen:
            return num
        seen.add(num)
    return None  # Return None if no duplicate is found

def find_product(arr):
    # Create a dictionary to store the frequency of each element
    frequency = {}
    for num in arr:
        if num in frequency:
            frequency[num] += 1
        else:
            frequency[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    for num, count in frequency.items():
        if count == 1:
            product *= num

    return product

# Example usage
arr = [1, 2, 3, 2, 4, 5, 5]
result = find_product(arr)
print(result)  # Output: 12 (1 * 3 * 4)

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n-1)

def binomial_coeff(n, k):
    if k > n:
        return 0
    if k == 0 or k == n:
        return 1
    k = min(k, n - k)  # Take advantage of symmetry
    c = 1
    for i in range(k):
        c = c * (n - i) // (i + 1)
    return c

