def find_sum(arr):
    # Create a dictionary to store the frequency of each element
    frequency = {}
    for num in arr:
        if num in frequency:
            frequency[num] += 1
        else:
            frequency[num] = 1

    # Calculate the sum of elements that appear more than once
    sum_repeated = 0
    for num, count in frequency.items():
        if count > 1:
            sum_repeated += num * count

    return sum_repeated

