def max_len_sub(arr):
    if not arr:
        return 0

    # Dictionary to store the length of subsequence ending with each element
    subseq_length = {}

    # Initialize the dictionary with 1 for each element
    for num in arr:
        subseq_length[num] = 1

    # Iterate through the array to build the subsequence lengths
    for num in arr:
        # Check if there's a subsequence ending with num-1
        if num - 1 in subseq_length:
            subseq_length[num] = max(subseq_length[num], subseq_length[num - 1] + 1)
        # Check if there's a subsequence ending with num+1
        if num + 1 in subseq_length:
            subseq_length[num + 1] = max(subseq_length[num + 1], subseq_length[num] + 1)

    # The maximum length of subsequence with difference of 1 between adjacent elements
    return max(subseq_length.values())

# Example usage
arr = [1, 2, 3, 4, 5, 3, 2, 1]
print(max_len_sub(arr))  # Output: 5 (subsequence: [1, 2, 3, 2, 1])
