def is_woodall(n):
    if n < 0:
        return []
    def woodall_numbers(limit):
        i = 0
        while True:
            woodall_num = i * 2**i - 1
            if woodall_num > limit:
                break
            yield woodall_num
            i += 1
    return list(woodall_numbers(n))

