def find_product(arr):
    # Create a dictionary to store the frequency of each element
    freq = {}
    for num in arr:
        if num in freq:
            freq[num] += 1
        else:
            freq[num] = 1

    # Initialize the product to 1
    product = 1
    # Initialize a counter for negative numbers
    negative_count = 0

    # Multiply the product by each non-repeated element
    for num, count in freq.items():
        if count == 1:
            if num < 0:
                negative_count += 1
            product *= num

    # If an odd number of negative numbers, negate the product
    if negative_count % 2 != 0:
        product *= -1

    return product

