def binomial_coeff(n, k, memo={}):
    if k == 0 or n == k:
        return 1
    if (n, k) in memo:
        return memo[(n, k)]
    result = binomial_coeff(n - 1, k - 1, memo) + binomial_coeff(n - 1, k, memo)
    memo[(n, k)] = result
    return result

