import heapq

def func(nums, k):
    if k == 0:
        return []

    # Create a dictionary to store the frequency of each number
    freq = {}
    for num in nums:
        if num in freq:
            freq[num] += 1
        else:
            freq[num] = 1

    # Use a heap to find the k most frequent numbers
    heap = []
    for num, count in freq.items():
        if len(heap) < k:
            heapq.heappush(heap, (count, num))
        else:
            if count > heap[0][0]:
                heapq.heapreplace(heap, (count, num))

    # Return the k most frequent numbers
    return [num for _, num in heap]

