def max_prime_factors(n, return_all=False):
    factors = []
    i = 2
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)

    if return_all:
        return factors
    else:
        return max(factors)

