def find_missing(arr):
    missing_numbers = []
    left, right = 0, len(arr) - 1
    while left <= right:
        if arr[left] != left + 1:
            # If the first element is missing, add all numbers up to the first missing one
            while left <= right and arr[left] != left + 1:
                missing_numbers.append(left + 1)
                left += 1
        else:
            left += 1
        if left > right:
            break
        if arr[right] != right + 1:
            # If the last element is missing, add all numbers from the last missing one to the last element
            while left <= right and arr[right] != right + 1:
                missing_numbers.append(right + 1)
                right -= 1
        else:
            right -= 1
    return missing_numbers

