            even = i
        elif i % 2 != 0 and odd is None:
            odd = i
        if even is not None and odd is not None:
            break
    if even is None or odd is None:
        return "List does not contain both even and odd numbers"
    else:
        # Ensure the division is performed with the correct sign
        return odd / abs(even)

from collections import Counter

def rearrange_string(s):
    # Check if the string has an even length
    if len(s) % 2 != 0:
        return []

    # Count the frequency of each character
    freq = Counter(s)

    # Check if the frequency of each character is even
    for char, count in freq.items():
        if count % 2 != 0:
            return []

    # Sort the characters in the string to ensure we can swap pairs
    sorted_chars = sorted(s)

    # Find the indices of characters that need to be swapped
    swap_indices = []
    for i in range(0, len(sorted_chars), 2):
        if sorted_chars[i] != sorted_chars[i + 1]:
            swap_indices.append((i, i + 1))

    # Generate all possible rearrangements
    rearrangements = []
    for indices in generate_permutations(swap_indices):
        rearranged_chars = list(sorted_chars)
        for i, j in indices:
            rearranged_chars[i], rearranged_chars[j] = rearranged_chars[j], rearranged_chars[i]
        rearrangements.append(''.join(rearranged_chars))

    return rearrangements

def generate_permutations(indices):
    if not indices:
        yield []
    else:
        for i in range(len(indices)):
            for sub_permutation in generate_permutations(indices[:i] + indices[i + 1:]):
                yield [indices[i]] + sub_permutation

