from collections import Counter

def flatten(nested_list):
    for sublist in nested_list:
        if isinstance(sublist, list):
            yield from flatten(sublist)
        else:
            yield sublist

def freq_element(nested_list):
    # Flatten the nested list
    flat_list = list(flatten(nested_list))
    # Filter out non-integer elements
    flat_list = [x for x in flat_list if isinstance(x, int)]
    # Use Counter to get the frequency of each integer
    freq = Counter(flat_list)
    return freq

def find_sum(arr):
    # Create a dictionary to keep track of the frequency of each element
    freq = {}
    for num in arr:
        if num in freq:
            freq[num] += 1
        else:
            freq[num] = 1

    # Sum up the elements that appear more than once
    sum_repeated = 0
    for num, count in freq.items():
        if count > 1:
            sum_repeated += num * count

    return sum_repeated

