def get_gcd(arr):
    def gcd(a, b):
        while b != 0:
            a, b = b, a % b
        return a

    # Filter out zeros and calculate GCD of non-zero elements
    non_zero_elements = [num for num in arr if num != 0]
    if not non_zero_elements:
        return 0  # Return 0 if all elements are zero

    result = non_zero_elements[0]
    for i in range(1, len(non_zero_elements)):
        result = gcd(result, non_zero_elements[i])

    return result

from math import isclose

def test_distinct(lst, rel_tol=1e-9, abs_tol=0.0):
    if not lst:
        return True
    seen = set()
    for num in lst:
        for seen_num in seen:
            if isclose(num, seen_num, rel_tol=rel_tol, abs_tol=abs_tol):
                return False
        seen.add(num)
    return True

