def remove_occ(s: str, ch: str) -> str:
    if not isinstance(ch, str) or len(ch) != 1:
        raise ValueError('Second parameter must be a single character.')
    if ch not in s:
        return s
    first_index = s.find(ch)
    last_index = s.rfind(ch)
    if first_index == last_index:
        return s[:first_index] + s[first_index + 1:]
    else:
        return s[:first_index] + s[first_index + 1:last_index] + s[last_index + 1:]

def split_lowerstring(text: str) -> list:
    if not isinstance(text, str):
        raise TypeError("Input must be a string.")
    return [text[i:j] for i, j in zip(range(len(text)), range(1, len(text) + 1)) if text[i].islower()]

import re

def text_lowercase_underscore(text: str) -> str:
    if not isinstance(text, str):
        raise TypeError("Input must be a string.")
    pattern = r'\b[a-z]+\_[a-z]+\b'
    if re.search(pattern, text):
        return 'Found a match!'
    else:
        return 'Not matched!'

def is_woodall(x: int) -> bool:
    if not isinstance(x, int):
        raise TypeError("Input must be an integer.")
    if x < 0:
        return False
    # Solve for n in the Woodall number formula: n * 2^n - 1 = x
    # Rearrange to find n: n = (1 + sqrt(1 + 8*x)) / 2
    n = (1 + (1 + 8 * x) ** 0.5) / 2
    # Check if n is a non-negative integer
    return n.is_integer()

def multiples_of_num(m: int, n: int) -> list:
    if m < 0 or n < 0:
        raise ValueError("Both m and n must be non-negative integers.")
    return [n * i for i in range(1, m + 1)]

def binary_to_decimal(binary: str) -> int:
    if not isinstance(binary, str) or not all(c in '01' for c in binary):
        raise ValueError('Invalid binary number')
    return int(binary, 2)

def max_prime_factors(n: int) -> int:
    if not isinstance(n, int) or n <= 0:
        raise ValueError('Input must be a positive integer')
    return _prime_factors(n)

def _prime_factors(n: int) -> int:
    i = 2
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
    return n

def decimal_to_binary(n: int) -> str:
    if not isinstance(n, int) or n < 0:
        raise ValueError("Input must be a non-negative integer.")
    if n == 0:
        return '0'

    binary = ''
    while n > 0:
        binary = str(n % 2) + binary
        n = n // 2
    return binary

