def find_rect_num(n: int) -> int:
    if not isinstance(n, int) or n <= 0:
        raise ValueError("Input must be a positive integer.")
    if n == 1:
        return 2
    return (n * (n + 1)) * 2

def find_nth_digit(p: int, q: int, N: int) -> int:
    if q == 0:
        raise ValueError('Denominator cannot be zero')

    # Convert the numbers to strings
    p_str = str(p)
    q_str = str(q)

    # Concatenate the two numbers
    fraction_str = p_str + q_str

    # Check if n is a valid index
    if N < len(fraction_str):
        # Return the nth digit as an integer
        return int(fraction_str[N])
    else:
        # Return None if n is out of range
        return None

def div_even_odd(lst: list) -> float:
    even = None
    odd = None
    for i in lst:
        if even is None and i % 2 == 0:
            even = i
        if odd is None and i % 2 != 0:
            odd = i
        if even is not None and odd is not None:
            break
    if even is None or odd is None:
        raise ValueError('List must contain at least one even and one odd number.')
    return odd / even if even != 0 else 0

from collections import Counter
from typing import List, Dict

def freq_element(nums: List[List[int]]) -> Dict[int, int]:
    return Counter(item for sublist in nums for item in sublist)

def find_sum(arr: list) -> int:
    if not isinstance(arr, list):
        raise TypeError("Input must be a list.")
    if not arr:
        return 0

    seen = set()
    sum_repeated = 0

    for num in arr:
        if num in seen:
            sum_repeated += num
        else:
            seen.add(num)

    return sum_repeated

def gcd(a: int, b: int) -> int:
    while b != 0:
        a, b = b, a % b
    return a

def get_gcd(numbers: list[int]) -> int:
    if not numbers:
        raise ValueError('Input list cannot be empty.')

    result = numbers[0]
    for num in numbers[1:]:
        result = gcd(result, num)
    return result

def check_equilateral(a: int, b: int, c: int) -> bool:
    if a <= 0 or b <= 0 or c <= 0:
        raise ValueError("All sides of a triangle must be positive integers.")
    return a == b == c

