        raise TypeError("Input must be a string.")
    return [word for word in text.split() if word[0].islower()]

def remove_dirty_chars(string_to_clean: str, dirty_chars: str) -> str:
    if not isinstance(string_to_clean, str) or not isinstance(dirty_chars, str):
        raise TypeError('Both inputs must be strings.')
    if not string_to_clean.isalpha() or not dirty_chars.isalpha():
        raise ValueError('Input strings must only contain alphabetic characters.')
