from typing import List

def func(nums: List[List[int]], k: int) -> List[int]:
    if not any(nums):
        return []

    if k > len(set(num for sublist in nums for num in sublist)):
        raise ValueError('k cannot be greater than the number of distinct integers')

    freq = {}
    for sublist in nums:
        for num in sublist:
            freq[num] = freq.get(num, 0) + 1

    heap = []
    for num, count in freq.items():
        if len(heap) < k:
            heapq.heappush(heap, (count, num))
        else:
            if count > heap[0][0]:
                heapq.heapreplace(heap, (count, num))

    return [num for _, num in heap]

def decimal_to_binary(n: int) -> str:
    if not isinstance(n, int) or n < 0:
        raise ValueError("Input must be a non-negative integer.")
    if n == 0:
        return '0'
    binary = ''
    while n > 0:
        binary = str(n % 2) + binary
        n = n // 2
    return binary

