def find_sum(arr: list[int]) -> int:
    if not isinstance(arr, list):
        raise TypeError("Input must be a list.")
    if not arr:
        return 0

    freq = {}
    for num in arr:
        if num in freq:
            freq[num] += 1
        else:
            freq[num] = 1

    sum_repeated = 0
    for num, count in freq.items():
        if count > 1:
            sum_repeated += num * count

    return sum_repeated

