def text_match(text: str) -> Optional[str]:
    pattern = '[_][a-z]+'
    if not isinstance(text, str):
        raise TypeError('Input must be a string.')
    if not text:
        return 'Not matched!'
    if re.search(pattern, text):
        return re.search(pattern, text).group()
    else:
        return 'Not matched!'

