def get_gcd(numbers: list[int]) -> int:
    if not numbers:
        raise ValueError('Input list cannot be empty.')

    def gcd(a: int, b: int) -> int:
        while b != 0:
            a, b = b, a % b
        return a

    result = numbers[0]
    for num in numbers[1:]:
        result = gcd(result, num)

    return result

