def find_max_num(digits: list[int], n: int) -> int:
    if not digits:
        return 0
    if n > len(digits):
        raise ValueError('Number of digits exceeds the length of the list.')
    sorted_digits = sorted(digits, reverse=True)
    return int(''.join(map(str, sorted_digits[:n])))

def is_octagonal(n: int) -> int:
    if not isinstance(n, int) or n <= 0:
        raise ValueError("Input must be a positive integer.")
    return n * (4 * n - 3) // 2

def max_len_sub(arr: list[int]) -> int:
    if not isinstance(arr, list) or not all(isinstance(x, int) for x in arr):
        raise TypeError("The first argument must be a list of integers.")
    if not arr:
        return 0

    n = len(arr)
    dp = [1] * n
    for i in range(1, n):
        for j in range(0, i):
            if arr[i] >= arr[j] and dp[i] < dp[j] + 1:
                dp[i] = dp[j] + 1
    return max(dp)
