import math

def is_woodall(n):
    if not isinstance(n, int) or n < 0:
        raise TypeError("Input must be a non-negative integer.")
    if n == 0:
        return True  # 0 is a Woodall number (0 * 2^0 - 1 = 0)
    if n == 1:
        return True  # 1 is a Woodall number (1 * 2^1 - 1 = 1)
    # Use the formula to check if n is a Woodall number
    log_x_plus_1_over_n = math.log2(n + 1) / n
    return log_x_plus_1_over_n.is_integer()

