import heapq

def func(nums, k):
    if not any(nums):
        return []

    # Create a dictionary to store the frequency of each number
    freq = {}
    for num_list in nums:
        if not num_list:
            continue
        for num in num_list:
            if num in freq:
                freq[num] += 1
            else:
                freq[num] = 1

    # Check if k is greater than the number of distinct integers
    if k > len(freq):
        raise ValueError('k cannot be greater than the number of distinct integers')

    # Use a heap to find the k most frequent numbers
    heap = []
    for num, count in freq.items():
        if len(heap) < k:
            heapq.heappush(heap, (count, num))
        else:
            if count > heap[0][0]:
                heapq.heapreplace(heap, (count, num))

    # Return the k most frequent numbers
    return [num for _, num in heap]

