def min_length_list(lst):
    if not isinstance(lst, list) or not all(isinstance(sub_lst, list) for sub_lst in lst):
        raise ValueError('Input must be a list of lists.')

    # Find the minimum length and the list with that length
    min_length = min(len(sub_lst) for sub_lst in lst)
    min_length_lists = [sub_lst for sub_lst in lst if len(sub_lst) == min_length]

    # Return the first list with the minimum length if there are multiple such lists
    return (min_length, min_length_lists[0]) if len(min_length_lists) > 1 else (min_length, min_length_lists[0])

