def find_all_duplicates(nums):
    seen = set()
    duplicates = set()

    for num in nums:
        if num in seen:
            duplicates.add(num)
        else:
            seen.add(num)

    # Return the duplicates in the order they first appeared
    result = []
    for num in nums:
        if num in duplicates and num not in result:
            result.append(num)

    return result

# Example usage:
nums = [2, 3, 1, 4, 5, 6, 7, 8, 9, 2, 3, 4]
print(find_all_duplicates(nums))  # Output should be [2, 3, 4]

def binomial_coeff(n, k):
    # Create a 2D list to store intermediate results
    dp = [[0] * (k + 1) for _ in range(n + 1)]

    # Base cases
    for i in range(n + 1):
        for j in range(min(i, k) + 1):
            if j == 0 or j == i:
                dp[i][j] = 1
            else:
                dp[i][j] = dp[i - 1][j - 1] + dp[i - 1][j]

    return dp[n][k]

# Example usage:
print(binomial_coeff(5, 2))  # Output: 10
print(binomial_coeff(10, 3)) # Output: 120

from collections import Counter

def flatten_and_count(nested_list):
    def _flatten(sublist):
        if isinstance(sublist, list):
            for item in sublist:
                yield from _flatten(item)
        else:
            yield sublist

    # Flatten the list of lists into a single list
    flat_list = list(_flatten(nested_list))

    # Use Counter to count the frequency of each integer
    frequency_counter = Counter(filter(lambda x: isinstance(x, int), flat_list))

    return dict(frequency_counter)

# Example usage:
nested_lists = [[1, 2, [3]], [4, [5, 6], 1], [1, [2, 7], [8, 9, 10]], [[[11]]]]
result = flatten_and_count(nested_lists)
print(result)  # Output: {1: 3, 2: 2, 3: 1, 4: 1, 5: 1, 6: 1, 7: 1, 8: 1, 9: 1, 10: 1, 11: 1}

def find_sum(arr):
    # Step 1: Create a dictionary to keep track of the count of each element
    element_count = {}

    # Step 2: Count the occurrences of each element
    for element in arr:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # Step 3: Calculate the sum of elements that appear more than once
    sum_of_repeated_elements = sum(element * count for element, count in element_count.items() if count > 1)

    return sum_of_repeated_elements

# Example usage:
arr = [1, -2, 3, -2, 4, 5, -5, 6, 7, 8, -8]
result = find_sum(arr)
print("Sum of repeated elements:", result)  # Output should be -2 + (-2) + 5 + (-5) + 8 + (-8) = -4

import math

def is_octagonal(n):
    if isinstance(n, list):
        return [octagonal_number(i) for i in range(1, n[0] + 1)]
    else:
        return octagonal_number(n)

def octagonal_number(n):
    return n * (3 * n - 2)

# Example usage:
print(is_octagonal([1]))   # [1]
print(is_octagonal([2]))   # [1, 8]
print(is_octagonal([3]))   # [1, 8, 22]
print(is_octagonal([4]))   # [1, 8, 22, 45]
print(is_octagonal(3))     # 22
