from typing import List

def sort_matrix(matrix: List[List[int]]) -> List[List[int]]:
    """
    Sorts a given matrix in ascending order based on the sum of its rows.

    :param matrix: A list of lists representing the matrix, where each sublist contains integers.
    :type matrix: list[list[int]]
    :return: The sorted matrix.
    :rtype: list[list[int]]
    :raises TypeError: If the input is not a list of lists or if any element in the matrix is not an integer.
    """
    if not isinstance(matrix, list):
        raise TypeError('Input must be a list.')

    for row in matrix:
        if not isinstance(row, list):
            raise TypeError('Each element in the matrix must be a list.')
        for elem in row:
            if not isinstance(elem, int):
                raise TypeError('All elements in the matrix must be integers.')

    # Handling empty matrix
    if not matrix:
        return []

    # Sorting the matrix based on the sum of each row
    sorted_matrix = sorted(matrix, key=sum)
    return sorted_matrix

