def text_match(text: str) -> str:
    # Define the pattern for lowercase letters joined with an underscore
    pattern = '^[a-z]+_[a-z]+$'

    # Check if the input is a string
    if not isinstance(text, str):
        raise TypeError('Input must be a string.')

    # Handle empty string input
    if not text:
        return 'Not matched!'

    # Search for the pattern in the given text
    if re.search(pattern, text):
        return 'Matched!'
    else:
        return 'Not matched!'

from typing import List

def test_distinct(numbers: List[int]) -> bool:
    """
    Determine if all numbers in the given list are distinct.

    :param numbers: List of integers.
    :type numbers: List[int]
    :return: True if all numbers are distinct, False otherwise.
    :rtype: bool
    """
    if not isinstance(numbers, list):
        raise TypeError("Input must be a list.")

    for num in numbers:
        if not isinstance(num, int):
            raise TypeError("All elements in the list must be integers.")

    return len(numbers) == len(set(numbers))

