def test_distinct(numbers: list) -> bool:
    """
    Determine if all numbers in the given list are distinct.

    :param numbers: List of integers or floats.
    :return: True if all numbers are distinct, False otherwise.
    """
    if not isinstance(numbers, list):
        raise TypeError("Input must be a list.")

    for number in numbers:
        if not isinstance(number, (int, float)):
            raise TypeError("All elements in the list must be integers or floats.")

    return len(numbers) == len(set(numbers))

def max_len_sub(arr):
    """
    Find the maximum length of the subsequence with difference between adjacent elements.

    :param arr: List[int] - A list of integers.
    :return: int - The maximum length of the subsequence.
    :raises TypeError: If the input is not a list or if the second parameter is not an integer.
    """
    if not isinstance(arr, list):
        raise TypeError("Input must be a list.")

    if not all(isinstance(x, int) for x in arr):
        raise TypeError("All elements in the list must be integers.")

    if not arr:
        return 0

    # Dictionary to store the length of the longest subsequence ending with a particular element
    dp = {}

    # Iterate through the array
    for num in arr:
        # The length of the subsequence ending with `num` can be derived from the lengths of subsequences
        # ending with `num-1` or `num+1`, whichever is available in the dictionary.
        dp[num] = 1 + max(dp.get(num - 1, 0), dp.get(num + 1, 0))

    # Return the maximum value from the dictionary, which represents the length of the longest subsequence
    return max(dp.values())
