def find_first_duplicate(nums):
    if not isinstance(nums, list):
        raise TypeError('Input must be a list.')
    if not all(isinstance(x, int) for x in nums):
        raise TypeError('All elements in the list must be integers.')

    if len(nums) == 0:
        return -1

    seen = set()

    for num in nums:
        if num in seen:
            return num
        seen.add(num)

    return -1

