print(find_first_duplicate(nums))  # Output should be -1

from collections import Counter

def rearrange_string(s):
    # Validate input type and content
    if not isinstance(s, str):
        raise ValueError('Input must be a string.')
    if not s.islower():
        raise ValueError('String must contain only lowercase letters.')

    # Count the frequency of each character in the string
    char_count = Counter(s)

    # Check if any character appears more than half the length of the string
    max_char_count = max(char_count.values())
    if max_char_count > (len(s) + 1) // 2:
        return ''

    # If there's only one unique character or all other characters appear less frequently,
    # it's always possible to rearrange the string as required.
    if len(set(s)) == 1 or (max_char_count == (len(s) + 1) // 2 and len(set(s)) == 2):
        return s[0] * (len(s) // 2) + s[-1] * (len(s) - len(s) // 2)

    # Try to place the most frequent character first
    result = [None] * len(s)
    index = 0
    for char, freq in char_count.items():
        while freq > 0:
            if index >= len(s):
                index = 1
            result[index] = char
            index += 2
            freq -= 1

    return ''.join(result)

# Example usage:
print(rearrange_string("aabbcc"))  # Output: "abacbc" or similar
print(rearrange_string("aaabc"))   # Output: ""
print(rearrange_string("aaa"))     # Output: ""

from collections import Counter

def freq_element(nested_list):
    if not isinstance(nested_list, list):
        raise TypeError('Input must be a list of lists.')

    # Flatten the list of lists into a single list
    flat_list = [item for sublist in nested_list for item in sublist]

    # If the flattened list is empty, return an empty dictionary
    if not flat_list:
        return {}

    # Use Counter to count the frequency of each element
    frequency_counter = Counter(flat_list)

    return dict(frequency_counter)

# Example usage:
nested_lists = []
result = freq_element(nested_lists)
print(result)  # Output: {}

nested_lists = [[1, 2, 3], [4, 5, 6], [1, 2, 7], [8, 9, 10]]
result = freq_element(nested_lists)
print(result)  # Output: {1: 2, 2: 2, 3: 1, 4: 1, 5: 1, 6: 1, 7: 1, 8: 1, 9: 1, 10: 1}

def test_distinct(numbers):
    """
    Determine if all numbers in the given list are distinct.

    :param numbers: List of integers or floats.
    :return: True if all numbers are distinct, False otherwise.
    :raises TypeError: If the input is not a list or if the list contains non-integer elements.
    """
    if not isinstance(numbers, list):
        raise TypeError("Input must be a list.")

    if not all(isinstance(x, (int, float)) for x in numbers):
        raise TypeError("All elements in the list must be integers or floats.")

    if len(numbers) <= 1:
        return True

    return len(numbers) == len(set(numbers))

def min_length_list(list_of_lists):
    if not isinstance(list_of_lists, list) or not all(isinstance(sublist, list) for sublist in list_of_lists):
        raise ValueError('Input must be a list of lists.')

    # Use the min function with a key argument to find the list with the minimum length
    return min(list_of_lists, key=len)

