def split_lowerstring(text: str) -> list[str]:
    result = []
    current_word = ""

    for char in text:
        if char.islower():
            if current_word:
                result.append(current_word)
            current_word = char
        else:
            current_word += char

    if current_word:
        result.append(current_word)

    return result

def remove_dirty_chars(string: str, second_string: str) -> str:
    # Convert second_string to a set for O(1) lookup
    chars_to_remove = set(second_string)

    # Use list comprehension to keep only characters not in chars_to_remove
    return ''.join(char for char in string if char not in chars_to_remove)

def find_first_duplicate(nums: list) -> int:
    seen = set()
    for num in nums:
        if num in seen:
            return num
        seen.add(num)
    return -1

def maximum_sum(list1: list[list[int]]) -> int:
    if not list1:  # Check if input list is empty
        return 0

    return max(sum(sublist) for sublist in list1)

