    for element in arr[:arr_size]:
        result ^= element
    return result

def count_substring_with_equal_ends(s: str) -> int:
    if not s:
        return 0

    n = len(s)
    count = 0

    # Consider each possible substring
    for i in range(n):
        for j in range(i, n):
            # If first and last characters match, increment count
            if s[i] == s[j]:
                count += 1

    return count

def max_prime_factors(n: int) -> int:
    largest_prime = -1

    # Handle division by 2 separately to simplify the loop
    while n % 2 == 0:
        largest_prime = 2
        n = n // 2

    # Check odd numbers up to sqrt(n)
    for i in range(3, int(n ** 0.5) + 1, 2):
        while n % i == 0:
            largest_prime = i
            n = n // i

    # If n is still greater than 2, it's prime itself
    if n > 2:
        largest_prime = n

    return largest_prime

def find_missing(ar: list, N: int) -> int:
    # Create a set from the array for O(1) lookup
    num_set = set(ar)

    # Check numbers from 1 to N to find the missing one
    for num in range(1, N + 1):
        if num not in num_set:
            return num

    return N + 1  # If no number is missing in range 1 to N

def find_nth_digit(p: int, q: int, N: int) -> int:
    if q == 0:
        raise ValueError("Denominator cannot be zero")

    # Handle negative numbers
    if p * q < 0:
        p, q = abs(p), abs(q)

    # Get the decimal digits
    quotient = p // q
    remainder = p % q

    # If N is 0, return the digit before decimal point
    if N == 0:
        return int(str(quotient)[-1])

    # Calculate decimal digits
    decimal_digits = ""
    remainders = {}
    position = 0

    while remainder != 0 and position < N + 1:
        # Check for repeating decimals
        if remainder in remainders:
            break

        remainders[remainder] = position
        remainder *= 10
        decimal_digits += str(remainder // q)
        remainder %= q
        position += 1

    # If we have enough digits
    if N <= len(decimal_digits):
        return int(decimal_digits[N-1])
    else:
        return 0  # If the decimal terminates before Nth position

from typing import Union, List

def div_even_odd(list1: List[int]) -> Union[float, str]:
    # Initialize variables to store first even and odd numbers
    first_even: Union[int, None] = None
    first_odd: Union[int, None] = None

    # Find first even and odd numbers
    for num in list1:
        if first_even is None and num % 2 == 0:
            first_even = num
        if first_odd is None and num % 2 != 0:
            first_odd = num
        if first_even is not None and first_odd is not None:
            break

    # Check if both even and odd numbers were found
    if first_even is None or first_odd is None:
        return "Cannot perform division: missing even or odd number"

    # Perform division and return result
    return first_even / first_odd

