from itertools import chain

def freq_element(nums: list[list[int]]) -> dict[int, int]:
    # Flatten the list of lists and count elements using Counter
    flat_list = list(chain.from_iterable(nums))
    frequency = Counter(flat_list)
    return dict(frequency)

