def find_sum(arr: list, n: int) -> int:
    # Create a dictionary to store element frequencies
    freq = {}

    # Count frequency of each element
    for num in arr:
        freq[num] = freq.get(num, 0) + 1

    # Calculate sum of repeated elements
    repeated_sum = sum(num for num, count in freq.items() if count > 1)

    return repeated_sum

import re

def text_match(text: str) -> str:
    pattern = r'^[a-z]+_[a-z]+$'
    if re.search(pattern, text):
        return "Found a match!"
    return "Not matched!"

from typing import List

def get_gcd(numbers: List[int]) -> int:
    from math import gcd
    from functools import reduce

    if not numbers:
        raise ValueError("Input list cannot be empty")

    # Handle negative numbers by using absolute values
    numbers = [abs(num) for num in numbers]

    # Reduce applies gcd function pairwise to the array elements
    return reduce(gcd, numbers)

def compute_last_digit(a: int, b: int) -> int:
    if a > b:
        return 0

    result = 1
    # We only need to multiply numbers from (a+1) to b
    # And we only care about the last digit at each step
    for i in range(a + 1, b + 1):
        result = (result * (i % 10)) % 10

    return result

def find_max_num(digits: list, n: int) -> int:
    # Ensure we have exactly n digits
    if len(digits) != n:
        raise ValueError(f"Expected {n} digits, but got {len(digits)} digits")

    # Convert digits to strings and sort in descending order
    digits_str = [str(d) for d in digits]
    digits_str.sort(reverse=True)

    # Join the sorted digits to form the largest number
    return int(''.join(digits_str))

def max_len_sub(arr: list[int]) -> int:
    if not arr or len(arr) < 2:
        return len(arr)

    n = len(arr)
    # dp[i] stores the length of subsequence ending at index i
    dp = [1] * n
    # Initialize the first element
    max_length = 1

    for i in range(1, n):
        for j in range(i):
            # Check if current element can be added to subsequence ending at j
            if abs(arr[i] - arr[j]) == 1:
                dp[i] = max(dp[i], dp[j] + 1)
        max_length = max(max_length, dp[i])

    return max_length
