def find_first_duplicate(arr):
    num_dict = {}
    for num in arr:
        if num in num_dict:
            return num
        num_dict[num] = True
    return None

def find_product(input_array):
    # Create a dictionary to store the frequency of each element
    element_frequency = {}
    for element in input_array:
        if element in element_frequency:
            element_frequency[element] += 1
        else:
            element_frequency[element] = 1

    # Initialize the product to 1
    product = 1

    # Multiply the product by each non-repeated element
    for element, count in element_frequency.items():
        if count == 1:
            product *= element

    return product

# Example usage:
print(find_product([1, 2, 3, 2, 1]))  # Output: 3
