def max_prime_factors(n):
    if n <= 1:
        raise ValueError("Input should be a positive integer greater than 1")

    # Initialize the maximum prime factor variable
    max_prime = -1

    # Check for number of 2s that divide n
    while n % 2 == 0:
        max_prime = 2
        n >>= 1  # equivalent to n //= 2

    # n must be odd at this point, thus skip even numbers and iterate only for odd integers
    for i in range(3, int(n**0.5) + 1, 2):
        while n % i == 0:
            max_prime = i
            n = n // i

    # This condition is to check if n is a prime number greater than 2
    if n > 2:
        max_prime = n

    return int(max_prime)

# Example usage
number = 315
print(f"The largest prime factor of {number} is {max_prime_factors(number)}")

def find_rect_num(n):
    if n < 0:
        raise ValueError("Invalid input: n must be a non-negative integer")
    return n * (n + 1)

