print("The length of the shortest list and the list itself are:", result)

def max_len_sub(arr, diff):
    if not arr:
        return 0

    # Dictionary to store the length of subsequence ending with each element
    subseq_length = {}

    # Initialize the dictionary with 1 for each element
    for num in arr:
        subseq_length[num] = 1

    # Iterate through the array to build the subsequence lengths
    for num in arr:
        # Check if there's a subsequence ending with num-diff
        if num - diff in subseq_length:
            subseq_length[num] = max(subseq_length[num], subseq_length[num - diff] + 1)
        # Check if there's a subsequence ending with num+diff
        if num + diff in subseq_length:
            subseq_length[num + diff] = max(subseq_length[num + diff], subseq_length[num] + 1)

    # The maximum length of subsequence with specified difference between adjacent elements
    return max(subseq_length.values())

# Example usage
arr = [1, 2, 3, 4, 5, 3, 2, 1]
diff = 1
print(max_len_sub(arr, diff))  # Output: 5 (subsequence: [1, 2, 3, 2, 1])
