def is_woodall(x: int) -> bool:
    if x < 0:
        return False

    n = 1
    while True:
        woodall_number = n * (2 ** n) - 1
        if woodall_number == x:
            return True
        elif woodall_number > x:
            return False
        n += 1

# Example usage:
print(is_woodall(7))  # True, because 7 is 1 * 2^3 - 1
print(is_woodall(23)) # True, because 23 is 3 * 2^3 - 1
print(is_woodall(10)) # False, 10 is not a Woodall number

from typing import List

def find_product(arr: List[int], n: int) -> int:
    # Dictionary to store the count of each element
    element_count = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in element_count:
            element_count[num] += 1
        else:
            element_count[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    has_non_repeated = False
    for num, count in element_count.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    # If there are no non-repeated elements, return 0
    return product if has_non_repeated else 0

# Example usage:
arr = [2, 3, 4, 2, 5, 3, 6]
n = len(arr)
print(find_product(arr, n))  # Output: 120 (4 * 5 * 6)

from typing import List

def get_odd_occurrence(arr: List[int], arr_size: int) -> int:
    # Create a dictionary to store the count of each element
    count_dict = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in count_dict:
            count_dict[num] += 1
        else:
            count_dict[num] = 1

    # Find and return the element with an odd count
    for num, count in count_dict.items():
        if count % 2 != 0:
            return num

    # If no element is found, raise an exception
    raise ValueError("No element occurs an odd number of times")

# Example usage:
arr = [1, 2, 3, 2, 3, 1, 3]
arr_size = len(arr)
result = get_odd_occurrence(arr, arr_size)
print(result)  # Output: 3

def count_substring_with_equal_ends(s: str) -> int:
    # Dictionary to store the frequency of each character
    frequency = {}

    # Count the frequency of each character in the string
    for char in s:
        if char in frequency:
            frequency[char] += 1
        else:
            frequency[char] = 1

    # Calculate the number of substrings for each character
    count = 0
    for char in frequency:
        n = frequency[char]
        count += (n * (n + 1)) // 2

    return count

# Example usage:
s = "abcab"
print(count_substring_with_equal_ends(s))  # Output: 7

from typing import List, Tuple, Union

def div_even_odd(list1: List[int]) -> Tuple[int, Union[int, float]]:
    first_even = None
    first_odd = None

    for num in list1:
        if first_even is None and num % 2 == 0:
            first_even = num
        if first_odd is None and num % 2 != 0:
            first_odd = num
        if first_even is not None and first_odd is not None:
            break

    if first_even is None:
        raise ValueError("No even number found in the list.")
    if first_odd is None:
        raise ValueError("No odd number found in the list.")

    division_result = first_even / first_odd
    return first_even, division_result

# Example usage:
numbers = [5, 3, 8, 6, 7]
result = div_even_odd(numbers)
print("First even number and its division by the first odd number:", result)

from typing import List

def find_sum(arr: List[int], n: int) -> int:
    # Create a dictionary to count occurrences of each element
    element_count = {}

    # Count each element in the array
    for element in arr:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # Calculate the sum of elements that are repeated
    repeated_sum = 0
    for element, count in element_count.items():
        if count > 1:
            repeated_sum += element * count

    return repeated_sum

# Example usage:
array = [1, 2, 3, 2, 4, 5, 6, 3, 3]
n = len(array)
print(find_sum(array, n))  # Output: 13 (2*2 + 3*3)
