def find_product(arr):
    """Calculate the product of non-repeated elements in an array."""

    # Dictionary to store the count of each element
    element_count = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in element_count:
            element_count[num] += 1
        else:
            element_count[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    has_non_repeated = False

    for num, count in element_count.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    # If there are no non-repeated elements, return 0
    return product if has_non_repeated else 0

# Example usage:
arr = [2, 3, 4, 2, 5, 3, 6]
print(find_product(arr))  # Output: 120 (4 * 5 * 6)

def find_sum(arr):
    """
    Calculate the sum of repeated elements in the given array.

    Parameters:
    arr (list): A list of integers.

    Returns:
    int: The sum of elements that are repeated in the array.
    """
    # Create a dictionary to count occurrences of each element
    element_count = {}

    # Count each element in the array
    for element in arr:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # Calculate the sum of elements that are repeated
    repeated_sum = 0
    for element, count in element_count.items():
        if count > 1:
            repeated_sum += element * count

    return repeated_sum


# Example usage:
if __name__ == "__main__":
    array = [1, 2, 3, 2, 4, 5, 6, 3, 3]
    print(find_sum(array))  # Output: 13 (2*2 + 3*3)
