def is_woodall(number):
    if not isinstance(number, int):
        raise TypeError("Input must be an integer.")

    if number < 0:
        return False

    n = 1
    while True:
        woodall_number = n * (2 ** n) - 1
        if woodall_number == number:
            return True
        elif woodall_number > number:
            return False
        n += 1

# Example usage:
try:
    print(is_woodall(7))  # True, because 7 is 1 * 2^3 - 1
    print(is_woodall(23)) # True, because 23 is 3 * 2^3 - 1
    print(is_woodall(10)) # False, 10 is not a Woodall number
    print(is_woodall(7.5)) # Raises TypeError
except TypeError as e:
    print(e)

def find_first_duplicate(arr):
    # Check if the input is a list
    if not isinstance(arr, list):
        raise TypeError("Input must be a list of integers.")

    # Check if all elements in the list are integers
    if not all(isinstance(num, int) for num in arr):
        raise TypeError("All elements in the list must be integers.")

    seen = set()
    for num in arr:
        if num in seen:
            return num
        seen.add(num)

    return None

# Example usage:
try:
    arr = [2, 1, 3, 5, 3, 2]
    print(find_first_duplicate(arr))  # Output: 3
except TypeError as e:
    print(e)

try:
    arr = [2, 1, '3', 5, 3, 2]  # This will raise a TypeError
    print(find_first_duplicate(arr))
except TypeError as e:
    print(e)  # Output: All elements in the list must be integers.

def maximum_sum(list_of_lists):
    # Check if the input is a list
    if not isinstance(list_of_lists, list):
        raise TypeError("Input must be a list of lists.")

    # Check if each element in the list is a list
    for inner_list in list_of_lists:
        if not isinstance(inner_list, list):
            raise TypeError("Input must be a list of lists.")

        # Check if each element in the inner list is an integer
        for element in inner_list:
            if not isinstance(element, int):
                raise TypeError("All elements in sublists must be integers.")

    # Initialize a variable to keep track of the maximum sum
    max_sum = float('-inf')

    # Iterate over each list in the list of lists
    for inner_list in list_of_lists:
        # Calculate the sum of the current inner list
        current_sum = sum(inner_list)

        # Update max_sum if the current_sum is greater
        if current_sum > max_sum:
            max_sum = current_sum

    return max_sum

# Example usage:
try:
    list_of_lists = [[1, 2, 3], [4, 5, 6], [7, 8, 9]]
    print(maximum_sum(list_of_lists))  # Output: 24
except TypeError as e:
