    print(find_product(arr))  # Output: 120 (4 * 5 * 6)

    arr_invalid = [2, 3, 'a', 5]
    print(find_product(arr_invalid))  # Raises TypeError
except TypeError as e:
    print(e)

def remove(strings):
    # Check if the input is a list
    if not isinstance(strings, list):
        raise TypeError("Input must be a list.")

    # Check if all elements in the list are strings
    for element in strings:
        if not isinstance(element, str):
            raise TypeError("All elements in the list must be strings.")

    # Create a translation table that maps each digit to None
    translation_table = str.maketrans('', '', '0123456789')

    # Use list comprehension to apply the translation table to each string
    return [s.translate(translation_table) for s in strings]

