def max_len_sub(arr, diff):
    # Check if the input array is a list
    if not isinstance(arr, list):
        raise TypeError("The first parameter must be a list.")

    # Check if the second parameter is an integer
    if not isinstance(diff, int):
        raise TypeError("The second parameter must be an integer.")

    if not arr:
        return 0

    # Dictionary to store the length of the longest subsequence ending at each index
    dp = [{} for _ in range(len(arr))]
    max_length = 1

    for i in range(len(arr)):
        for j in range(i):
            current_diff = arr[i] - arr[j]
            # Only consider subsequences with the specified difference
            if current_diff == diff:
                # If there is already a subsequence with this difference ending at j, extend it
                if current_diff in dp[j]:
                    dp[i][current_diff] = dp[j][current_diff] + 1
                else:
                    # Otherwise, start a new subsequence with length 2 (arr[j], arr[i])
                    dp[i][current_diff] = 2

                # Update the maximum length found so far
                max_length = max(max_length, dp[i][current_diff])

    return max_length

