def is_woodall(number):
    if number < 0:
        return False

    n = 1
    while True:
        woodall_number = n * (2 ** n) - 1
        if woodall_number == number:
            return True
        elif woodall_number > number:
            return False
        n += 1

# Example usage:
print(is_woodall(7))  # True, because 7 is 1 * 2^3 - 1
print(is_woodall(23)) # True, because 23 is 3 * 2^3 - 1
print(is_woodall(10)) # False, 10 is not a Woodall number

def find_product(arr):
    # Dictionary to store the count of each element
    element_count = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in element_count:
            element_count[num] += 1
        else:
            element_count[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    has_non_repeated = False
    for num, count in element_count.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    # If there are no non-repeated elements, return 0
    return product if has_non_repeated else 0

# Example usage:
arr = [2, 3, 4, 2, 5, 3, 6]
print(find_product(arr))  # Output: 120 (4 * 5 * 6)

def binomial_coeff(n, k):
    if not (isinstance(n, int) and isinstance(k, int)):
        raise ValueError("Both n and k must be integers.")
    if n < 0 or k < 0:
        raise ValueError("Both n and k must be non-negative integers.")
    if k > n:
        return 0
    if k == 0 or k == n:
        return 1

    # Use a more efficient approach to calculate the binomial coefficient
    # C(n, k) = C(n, n-k)
    k = min(k, n - k)

    c = 1
    for i in range(k):
        c = c * (n - i) // (i + 1)

    return c

# Example usage:
n = 5
k = 2
print(f"Binomial Coefficient C({n}, {k}) is {binomial_coeff(n, k)}")

def count_substring_with_equal_ends(s):
    # Dictionary to store the frequency of each character
    frequency = {}

    # Count the frequency of each character in the string
    for char in s:
        if char in frequency:
            frequency[char] += 1
        else:
            frequency[char] = 1

    # Calculate the number of substrings for each character
    count = 0
    for char in frequency:
        n = frequency[char]
        count += (n * (n + 1)) // 2

    return count

# Example usage:
s = "abcab"
print(count_substring_with_equal_ends(s))  # Output: 7

def div_even_odd(numbers):
    first_even = None
    first_odd = None

    for num in numbers:
        if first_even is None and num % 2 == 0:
            first_even = num
        if first_odd is None and num % 2 != 0:
            first_odd = num
        if first_even is not None and first_odd is not None:
            break

    if first_even is None:
        raise ValueError("No even number found in the list.")
    if first_odd is None:
        raise ValueError("No odd number found in the list.")

    # Perform the division and return the result
    return first_even / first_odd

# Example usage:
numbers = [5, 3, 8, 6, 7]
result = div_even_odd(numbers)
print("Division of first even and odd number:", result)

def find_sum(arr):
    # Create a dictionary to count occurrences of each element
    element_count = {}

    # Count each element in the array
    for element in arr:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # Calculate the sum of elements that are repeated
    repeated_sum = 0
    for element, count in element_count.items():
        if count > 1:
            repeated_sum += element * count

    return repeated_sum

# Example usage:
array1 = [1, 2, 3, 2, 4, 5, 6, 3, 3]
print(find_sum(array1))  # Output: 13 (2*2 + 3*3)

array2 = [1, 2, 3, 4, 5, 6]
print(find_sum(array2))  # Output: 0 (no repeated elements)

def max_len_sub(arr, allowed_diff):
    if not arr:
        return 0

    # Dictionary to store the length of the longest subsequence ending at each index
    dp = [1] * len(arr)  # Initialize with 1 because each element is a subsequence of length 1

    max_length = 1

    for i in range(1, len(arr)):
        for j in range(i):
            if abs(arr[i] - arr[j]) == allowed_diff:
                dp[i] = max(dp[i], dp[j] + 1)
                max_length = max(max_length, dp[i])

    return max_length

