def get_odd_occurrence(arr):
    # Handle empty list case
    if not arr:
        return None

    # Dictionary to store the count of each element
    count_dict = {}

    # Count the occurrences of each element
    for element in arr:
        if element in count_dict:
            count_dict[element] += 1
        else:
            count_dict[element] = 1

    # Find and return the element with an odd occurrence
    for element, count in count_dict.items():
        if count % 2 != 0:
            return element

    # If no element occurs an odd number of times, return None
    return None

# Example usage:
arr = [1, 2, 3, 2, 3, 1, 3]
print(get_odd_occurrence(arr))  # Output should be 3

empty_arr = []
print(get_odd_occurrence(empty_arr))  # Output should be None

def find_rect_num(n):
    """
    Calculate the n-th rectangular number.

    :param n: An integer representing the position in the sequence of rectangular numbers.
    :return: The n-th rectangular number.
    """
    if n == 1:
        return 2
    else:
        return n * (n + 1)

