def last_non_zero_digit(n):
    if n == 0 or n == 1:
        return 1

    # Handle small values directly
    if n < 5:
        fact = 1
        for i in range(2, n + 1):
            fact *= i
        return str(fact)[-1]

    # Compute last non-zero digit for n >= 5
    last_digit = 1
    for i in range(2, n + 1):
        while i % 5 == 0:
            i //= 5
        last_digit = (last_digit * (i % 10)) % 10

    # Adjust for powers of 2 left over
    power_of_two = 0
    for i in range(2, n + 1):
        while i % 2 == 0:
            i //= 2
            power_of_two += 1

    # Adjust based on the number of factors of 5 and 2
    if power_of_two > n // 5 + n // 25 + n // 125:
        last_digit *= 6
    elif power_of_two > n // 5 + n // 25:
        last_digit *= 4
    else:
        last_digit *= 2

    return last_digit % 10

def compute_last_digit(a, b):
    if a > b:
        return 0  # If a > b, the result would be a fraction, hence no last digit in integer context

    if a == b:
        return 1  # Edge case: a equals b, so the result is 1

    last_digit_a = last_non_zero_digit(a)
    last_digit_b = last_non_zero_digit(b)

    # Calculate the last digit of the division
    if last_digit_b != 0:
        # Find modular inverse of last_digit_b under modulo 10
        for i in range(1, 10):
            if (last_digit_b * i) % 10 == 1:
                inverse = i
                break

        result = (last_digit_a * inverse) % 10
    else:
        result = 0  # Division by zero case, though theoretically shouldn't happen

    return result

