def find_first_duplicate(nums: list) -> int:
    """
    Finds the first duplicate element in an array of integers.

    :param nums: List[int] - The array of integers to search for duplicates.
    :return: int - The first duplicate integer found.
             Raises ValueError if no duplicates exist.
    """
    seen = set()
    for num in nums:
        if num in seen:
            return num
        seen.add(num)
    raise ValueError("No duplicates found in the input list")

# Example usage:
if __name__ == "__main__":
    example_array = [2, 5, 1, 2, 3, 5, 1, 2, 4]
    try:
        print(find_first_duplicate(example_array))  # Output should be 2
    except ValueError as e:
        print(e)

def maximum_sum(list1: list[list[int]]) -> int:
    # Calculate the sum of each sublist and return the maximum sum
    return max(sum(sublist) for sublist in list1)

# Example usage:
example_list = [[1, 2, 3], [4, 5, 6], [7, 8, 9]]
print(maximum_sum(example_list))  # Output will be 24 because [7, 8, 9] has the highest sum.

from typing import List

def get_odd_occurrence(arr: List[int], arr_size: int) -> int:
    # Create a dictionary to store counts of each element
    count_dict: dict[int, int] = {}

    # Iterate through the array and update counts in the dictionary
    for num in arr:
        if num in count_dict:
            count_dict[num] += 1
        else:
            count_dict[num] = 1

    # Find and return the first element with an odd count
    for num, count in count_dict.items():
        if count % 2 != 0:
            return num

    # If no such element is found, raise an error or return a default value
    raise ValueError("No element occurs an odd number of times")

# Example usage:
arr = [1, 2, 3, 2, 3, 1, 3]
arr_size = len(arr)
print(get_odd_occurrence(arr, arr_size))  # Output should be 3

from typing import List, Union, Tuple

def div_even_odd(list1: List[int]) -> Union[Tuple[None, str], Tuple[float]]:
    # Initialize variables to store the first even and odd numbers
    first_even: int = None
    first_odd: int = None

    # Iterate through the list to find the first even and odd numbers
    for num in list1:
        if num % 2 == 0 and first_even is None:
            first_even = num
        elif num % 2 != 0 and first_odd is None:
            first_odd = num

        # Break the loop if both even and odd numbers are found
        if first_even is not None and first_odd is not None:
            break

    # Check if both even and odd numbers were found
    if first_even is None:
        return (None, "No even number found in the list.")
    if first_odd is None:
        return (None, "No odd number found in the list.")

    # Perform the division and return the result
    try:
        result = first_even / first_odd
    except ZeroDivisionError:
        return (None, "Cannot divide by zero.")

    return (result,)

# Example usage:
numbers_list = [7, 3, 4, 8, 9]
print(div_even_odd(numbers_list))  # Output: (1.3333333333333333,)

from functools import reduce
import math
from typing import List

def get_gcd(numbers: List[int]) -> int:
    if not numbers:
        raise ValueError("Array must not be empty")

    return reduce(lambda x, y: math.gcd(x, y), numbers)

# Example usage:
numbers = [24, 108, 90]
print(get_gcd(numbers))  # Output will be 6

def check_equality(s: str) -> str:
    # Check if the string is empty or has only one character
    if len(s) <= 1:
        return "Equal"

    # Compare the first and last character of the string
    if s[0] == s[-1]:
        return "Equal"
    else:
        return "Not Equal"

# Example usage:
print(check_equality("radar"))  # Output: "Equal"
print(check_equality("python")) # Output: "Not Equal"
