from collections import Counter

def freq_element(nested_list):
    # Flatten the list of lists into a single list
    flat_list = [item for sublist in nested_list for item in sublist]

    # Use Counter to count the frequency of each element
    return dict(Counter(flat_list))

# Example usage:
nested_lists = [[1, 2, 3], [4, 5, 6], [1, 2, 7], [8, 9, 10]]
result = freq_element(nested_lists)
print(result)  # Output: {1: 2, 2: 2, 3: 1, 4: 1, 5: 1, 6: 1, 7: 1, 8: 1, 9: 1, 10: 1}

def find_sum(arr):
    # Create a dictionary to count the occurrences of each element
    element_count = {}

    # Count the occurrences of each element
    for element in arr:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # Initialize sum of repeated elements
    sum_of_repeated_elements = 0

    # Calculate the sum of elements that appear more than once
    for element, count in element_count.items():
        if count > 1:
            sum_of_repeated_elements += element * count

    return sum_of_repeated_elements

# Example usage:
arr = [1, 2, 3, 2, 4, 5, 5, 6, 7, 8, 8]
result = find_sum(arr)
print("Sum of repeated elements:", result)  # Output should be 2 + 2 + 5 + 5 + 8 + 8 = 32
