from collections import Counter

def count_common(word_list):
    """
    Count and return the most common words in a given list of strings.

    :param word_list: A list of strings to analyze.
    :return: A list of tuples (word, frequency) for the most common words.
    :raises TypeError: If the input is not a list of strings.
    """
    # Check if the input is a list of strings
    if not isinstance(word_list, list) or not all(isinstance(word, str) for word in word_list):
        raise TypeError("Input must be a list of strings.")

    # Count the frequency of each word in the list
    word_counts = {}
    for word in word_list:
        if word in word_counts:
            word_counts[word] += 1
        else:
            word_counts[word] = 1

    # Use Counter to get the most common elements
    counter = Counter(word_counts)
    most_common_words = counter.most_common()

    return most_common_words

