def binomial_coeff(n, k):
    # Check if inputs are non-negative and k is not greater than n
    if n < 0 or k < 0:
        raise ValueError("Inputs must be non-negative")
    if k > n:
        raise ValueError("k must not be greater than n")

    # If k is more than n-k, then it's more efficient to calculate binomial_coeff(n, n-k)
    if k > n - k:
        k = n - k

    # Initialize result
    result = 1

    # Calculate value of [n*(n-1)*---*(n-k+1)] / [k*(k-1)*---*1]
    for i in range(k):
        result *= (n - i)
        result //= (i + 1)

    return result

# Example usage:
try:
    print(binomial_coeff(5, 2))  # Output: 10
    print(binomial_coeff(10, 3)) # Output: 120
    print(binomial_coeff(-1, 2)) # Raises ValueError
except ValueError as e:
    print(e)

try:
    print(binomial_coeff(5, 6))  # Raises ValueError
except ValueError as e:
