def div_even_odd(numbers):
    # Initialize variables to store the first even and odd numbers
    first_even = None
    first_odd = None

    # Iterate through the list to find the first even and odd numbers
    for num in numbers:
        if num % 2 == 0 and first_even is None:
            first_even = num
        elif num % 2 != 0 and first_odd is None:
            first_odd = num

    # Check if both first_even and first_odd are found
    if first_even is not None and first_odd is not None:
        # Perform the division and return the result
        return first_even / first_odd
    else:
        # Raise ValueError if neither an even nor an odd number is found
        if first_even is None and first_odd is None:
            raise ValueError("List must contain at least one even and one odd number.")
        elif first_even is None:
            raise ValueError("List must contain at least one even number.")
        elif first_odd is None:
            raise ValueError("List must contain at least one odd number.")

# Example usage
try:
    numbers = [5, 3, 2, 8, 10]
    print(div_even_odd(numbers))  # Output should be 2.0

    numbers = [7, 9, 11]
    print(div_even_odd(numbers))  # Raises ValueError

    numbers = [2, 4, 6]
    print(div_even_odd(numbers))  # Raises ValueError

    numbers = [2, 4, 6, 1, 3]
    print(div_even_odd(numbers))  # Output should be 2.0

    numbers = [2, 4, 6, 1, 3, 5]
    print(div_even_odd(numbers))  # Output should be 2.0

except ValueError as e:
